CREATE TABLE `travel_db`.`users` ( `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT , `firstName` VARCHAR(255) NOT NULL , `lastName` VARCHAR(255) NOT NULL , `email` VARCHAR(255) NOT NULL , `password` BLOB NOT NULL , `salt` BLOB NOT NULL , `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `role` INT(1) UNSIGNED NOT NULL DEFAULT '0' , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `travel_db`.`itinerary` ( `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT , `name` VARCHAR(255) NOT NULL , `description` VARCHAR(2048) NOT NULL , `duration` INT(11) UNSIGNED NOT NULL , `durationType` VARCHAR(1) NOT NULL , `createBy` INT(11) UNSIGNED NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `travel_db`.`sessions` ( `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT , `email` VARCHAR(255) NOT NULL , `expirationDate` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `userId` INT(11) NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `travel_db`.`subscriptions` ( `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT , `itineraryId` INT(11) UNSIGNED NOT NULL , `userId` INT(11) UNSIGNED NOT NULL , `subscriptionOn` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `travel_db`.`emailActivity` ( `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT , `userId` INT(11) UNSIGNED NOT NULL , `emailList` VARCHAR(1024) NOT NULL , `itineraryId` INT(11) UNSIGNED NOT NULL , `notificationType` VARCHAR(255) NOT NULL , `status` VARCHAR(255) NOT NULL , `emailBody` BLOB NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `travel_db`.`itineraryDayEvent` ( `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT , `itineraryDayId` INT(11) UNSIGNED NOT NULL , `createdBy` INT(11) UNSIGNED NOT NULL , `timeOfEvent` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `type` VARCHAR(255) NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `travel_db`.`site` ( `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT , `eventId` INT(11) UNSIGNED NOT NULL , `name` VARCHAR(255) NOT NULL , `description` VARCHAR(2048) NOT NULL , `address` VARCHAR(2048) NOT NULL , `link` VARCHAR(2048) NOT NULL , `startTime` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `duration` INT(11) UNSIGNED NOT NULL , `addedBy` INT(11) UNSIGNED NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `travel_db`.`hotel` ( `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT , `itineraryDayId` INT(11) UNSIGNED NOT NULL , `name` VARCHAR(255) NOT NULL , `address` VARCHAR(2048) NOT NULL , `phone` VARCHAR(20) NOT NULL , `link` VARCHAR(2048) NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;

